// Helper function -- normalizes make and model names
export function uniformArr(arr){
  return arr.map((i) => {
    const lowercase = i.toLowerCase().slice(1, i.length)
    const uppercase = i.charAt(0).toUpperCase()
    return uppercase + lowercase
  })
} 

export function convertToCamel (str) {
  const lowercase = str.toLowerCase().slice(1, str.length)
  const uppercase = str.charAt(0).toUpperCase()
  return uppercase + lowercase
}

// Fills an empty object with all current makes
export function fillMake(arr){

  const finalArr = []
  arr.forEach((i)=> {
    const newObj = {make: '', model: []}
    newObj.make = i;
    finalArr.push(newObj)
  })

  return finalArr
}

// FINAL ------ Fills object with all makes with their corresponding models
export function makeModelObj(finalMakes, allCars){

  const objArr = fillMake(finalMakes)

  allCars.forEach((car) => {
    objArr.forEach((obj) => {
      if (car.make.toLowerCase() == obj.make.toLowerCase())
      {
        obj.model.push(car.model)
        
      }
    })
  })

  const newObj = objArr.map(({make, model}, i) => {
    const newMake = make
    const newModel = [...new Set(model.map(i => i.toLowerCase()))]
    const final = {newMake, newModel}
    return final
  })

  return newObj
}







